import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store = new Vuex.Store({
    state: {
        backendUrl: "http://127.0.0.1:8000/api/v1",
        settings: ''
    },
    mutations: {
        setSittings (state, s) {
            console.log(s)
            state.settings = s
        }
    },
    actions: {},
    modules: {},
    getters: {
        getServerUrl: state => {
            return state.backendUrl
        },
        getSettings: state => {
            console.log(state.settings)
            return state.settings
        }
    }

})

export default store
