import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store = new Vuex.Store({
    state: {
        backendUrl: "http://127.0.0.1:8000/api/v1",
        current_post: 'qwerty'
    },
    mutations: {
        increment (current_post, fen) {
            state.current_post = fen
  } 
    },
    actions: {},
    modules: {},
    getters: {
        getServerUrl: state => {
            return state.backendUrl
        },
        get_pos: state => {
            return state.current_post
        }
    }

})

export default store
